import React from 'react';
import { useState, useEffect } from 'react';
import medical_research from '../Static/svg/medical_research_pink.svg';



function InitialSection (props) {

    const [data, setData] = useState(props.result);
    const [error, setError] = useState(props.error);
    const [errorDescription, setErrorDesc] = useState(props.errorDescription);
    return (
        <>
         <div className="container">
            <div className="columns">
                <div className="column">
                    <div className="centered">
                    <h2>Global Statistics</h2>

                    {data &&
                         <div className='is-result'>
                            <p><strong>New  Confirmed:</strong> {data.Global.NewConfirmed}</p>
                            <p><strong>Total Confirmed:</strong> {data.Global.TotalConfirmed}</p>
                            <p><strong>New  Deaths:</strong> {data.Global.NewDeaths}</p>
                            <p><strong>Total Deaths:</strong> {data.Global.TotalDeaths}</p>
                            <p><strong>New  Recovered:</strong> {data.Global.NewRecovered}</p>
                            <p><strong>Total  Recovered:</strong> {data.Global.TotalRecovered}</p>
                        </div>
                    }

                    {error &&
                        <>
                        <div className='is-error'>

                            <p>Ups!! Something was wrong</p>
                            <p>{errorDescription.message}</p>

                        </div>
                        </>
                    }

                    <button className="button is-primary">Dashboard</button>
                    </div>
                </div>
                <div className="column">
                    <figure className="image">
                        <img src={medical_research} alt="" />
                    </figure>
                </div>
            </div>
            </div>
        </>
    );
}

export default InitialSection;
