import React from 'react';
import { useState, useEffect } from 'react';
import InitialSection from '../Sections/InitialSection.js';
import HeaderComponent from '../Components/HeaderComponent.js';
import DashboardSection from '../Sections/DashboardSection.js';

const axios = require('axios');

function HomePage() {
    const [result, setResult] = useState();
    const [error, setError] = useState(false);
    const [errorDescription, setErrorDesc] = useState(false);

    const fetchData = async () => {
        await axios.get(`https://api.covid19api.com/summary`, {
            headers: {
                'Content-Type': 'application/json',
            }
        })
            .then(function (response) {
                setError(false);
                //  alert(JSON.stringify(response));
                setResult(response.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
                setErrorDesc(error);
                setError(true);

            })
            .finally(function () {
                // always executed
            });


    };

    useEffect(() => { fetchData(); },[]);

    return (
        <>
       <header>
            <HeaderComponent/>
       </header>
       <main>
            <section id='main_section'>
              <InitialSection />
            </section>
            <section id='dashboard_section'>
              <DashboardSection />
            </section>
        </main>
        </>
    );
}

export default HomePage;
