import React from 'react';
import { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import doctorsImg from '../Static/svg/doctors_pink.svg';
import { Bar } from 'react-chartjs-2';


const axios = require('axios');

function DashboardPage() {

    const [result, setResult] = useState();

    const fetchData = async () => {
        await axios.get(`https://api.covid19api.com/summary`, {
            headers: {
                'Content-Type': 'application/json',
            }
        })
            .then(function (response) {
                setError(false);
                //  alert(JSON.stringify(response));
                const resultBarToDraw = response.data.Countries.map(item => {

                    if (item.CountryCode === "IT" || item.CountryCode === "ES") {
                        const resultBar = {};
                        resultBar['x'] = item.Country;
                        resultBar['y'] = item.TotalConfirmed;
                        return resultBar.filter(x => x != null);
                    }else{

                    }


                })
                setResult(resultBarToDraw);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
                setErrorDesc(error);
                setError(true);

            })
            .finally(function () {
                // always executed
            });


    };

    useEffect(() => { fetchData(); }, []);


    return (
        <>
            <header>
                <h1>dashboard</h1>
            </header>
            <main>
                <div class="container">
                    <div className="columns">
                        <div className="column is-half">
                            <h3>Total Cases</h3>
                            {result &&
                                <>

                                    <Bar
                                        data={result}
                                        width={500}
                                        height={500}
                                        options={{ maintainAspectRatio: true }}
                                    />
                                    {JSON.stringify(result)}

                                </>
                            }

                        </div>
                        <div className="column">
                            <img src={doctorsImg} alt="" />
                            <Link to='/' className="button is-primary">
                                Home
                            </Link>
                        </div>
                    </div>
                </div>
            </main>
        </>
    );
}

export default DashboardPage;
