import React from 'react';
import { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import doctorsImg from '../Static/svg/doctors_pink.svg';
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, CartesianGrid } from 'recharts';


const axios = require('axios');

function DashboardPage() {

    const [result, setResult] = useState();
    const [error, setError] = useState(false);
    const [errorDescription, setErrorDesc] = useState(false);
    const fetchData = async () => {
        await axios.get(`https://api.covid19api.com/summary`, {
            headers: {
                'Content-Type': 'application/json',
            }
        })
            .then(function (response) {
                setError(false);
                //  alert(JSON.stringify(response));

                const resultDataAux = response.data.Countries.map(function (item, index) {

                    let resultDataset = {};
                    resultDataset["Country"] = item.Country;
                    resultDataset["Total Confirmed"] = item.TotalConfirmed;

                    return resultDataset;

                })

                /* const resultLabelAux = response.data.Countries.map(function (item, index) {

                         let resultLabels = {};
                         resultLabels = item.Country;

                         return resultLabels;

                 })*/
                //  const resultLabel = resultLabelAux.filter(x => x != null);
                const resultData = resultDataAux.filter(x => x != null);
                setResult(...resultData);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
                setErrorDesc(error);
                setError(true);

            })
            .finally(function () {
                // always executed
            });


    };

    useEffect(() => { fetchData(); }, []);


    return (
        <>
            <header>
                <h1>dashboard</h1>
            </header>
            <main>
                <div className="container">
                    <div className="columns">
                        <div className="column is-half">
                            <h3>Total Cases</h3>
                            {result &&
                                <>
                                    <BarChart width={600} height={300} data={result}>
                                        <XAxis dataKey="Country" stroke="#8884d8" />
                                        <YAxis />
                                        <Tooltip wrapperStyle={{ width: 100, backgroundColor: '#ccc' }} />
                                        <Legend width={100} wrapperStyle={{ top: 40, right: 20, backgroundColor: '#f5f5f5', border: '1px solid #d5d5d5', borderRadius: 3, lineHeight: '40px' }} />
                                        <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
                                        <Bar dataKey="Total Confirmed" fill="#8884d8" barSize={30} />
                                    </BarChart>

                                    {result}

                                </>
                            }

                        </div>
                        <div className="column">
                            <img src={doctorsImg} alt="" />
                            <Link to='/' className="button is-primary">
                                Home
                            </Link>
                        </div>
                    </div>
                </div>
            </main>
        </>
    );
}

export default DashboardPage;
