import React from 'react';
import { useState } from 'react';
import { Link } from "react-router-dom";
import doctorsImg from '../Static/svg/doctors_pink.svg';
import { LineChart, Line, Tooltip, Legend,XAxis, YAxis, CartesianGrid } from 'recharts';


const axios = require('axios');

function DashboardPage() {
    const [result, setResult] = useState();
    const [country, setCountryName] = useState();
    const [error, setError] = useState(false);
    const [errorDescription, setErrorDesc] = useState(false);

    const handleChange = (e) => {
        setCountryName(
            e.target.value
        )
    }

    const handleKey = (event) => {
        if (event.keyCode === 13) {
            handleClick();
        }
    }



    const handleClick = () => {

        axios.get(`https://api.covid19api.com/country/${country}`, {
            headers: {
                'Content-Type': 'application/json',
            }
        })
            .then(function (response) {
                setError(false);
                setResult(response);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
                setErrorDesc(error);
                setError(true);

            })
            .finally(function () {
                // always executed
            });


    };

    //useEffect(() => { fetchData(); }, []);


    return (
        <>
            <header>
                <h1>dashboard</h1>
            </header>
            <main>
                <div className="container">
                    <div className="columns">
                        <div className="column">
                            <div className="field">
                                <label className="label"></label>
                                <div className="control">
                                    <input id="country" name="country" className="input" type="text" placeholder="Country" onKeyDown={(e) => handleKey(e)} onChange={e => handleChange(e)} />
                                </div>
                            </div>
                            <div className="field is-grouped">
                                <div className="control">
                                    <button className="button is-primary" onClick={() => handleClick()}>submit</button>

                                </div>

                            </div>
                            {result &&
                                <>
                                    <div className="container">

                                        <LineChart width={1000} height={600} data={result.data}>
                                            <XAxis dataKey="Date" />
                                            <YAxis />
                                            <Tooltip />
                                            <Legend />
                                            <CartesianGrid stroke="#eee" strokeDasharray="5 5" />
                                            <Line type="monotone" dataKey="Confirmed" stroke="#4B4B4B" />
                                            <Line type="monotone" dataKey="Deaths" stroke="#FF57BB" />
                                            <Line type="monotone" dataKey="Recovered" stroke="#0091AD" />
                                            <Line type="monotone" dataKey="Active" stroke="#6EFAFB" />
                                        </LineChart>
                                    </div>

                                </>
                            }

                        </div>
                    </div>
                    <div className="columns">
                        <div className="colum">
                        </div>
                    </div>
                    <div className="columns">
                        <div className="colum">

                        </div>
                        <div className="column">
                            <img src={doctorsImg} alt="" />
                            <Link to='/' className="button is-primary">
                                Home
                            </Link>
                        </div>
                    </div>
                </div>

            </main>
        </>
    );
}

export default DashboardPage;
