import React from 'react';
import { useState, useEffect } from 'react';
import medical_research from '../Static/svg/medical_research_pink.svg';

const axios = require('axios');

function InitialSection() {
    const [result, setResult] = useState();

    const fetchData = async () => {
        await axios.get(`https://api.covid19api.com/summary`, {
            headers: {
                'Content-Type': 'application/json',
            }
        })
            .then(function (response) {
                //  alert(JSON.stringify(response));
                setResult(response.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);

            })
            .finally(function () {
                // always executed
            });


    };
    useEffect(() => { fetchData() });

    return (
        <>
            <div className="columns is-centered">
                <div className="column">
                    {result &&
                        <>
                            <p><label>New  Confirmed:</label> {result.Global.NewConfirmed}</p>
                            <p>Total Confirmed: {result.Global.TotalConfirmed}</p>
                            <p>New  Deaths: {result.Global.NewDeaths}</p>
                            <p>Total Deaths: {result.Global.TotalDeaths}</p>
                            <p>New  Recovered: {result.Global.NewRecovered}</p>
                            <p>Total  Recovered: {result.Global.TotalRecovered}</p>
                        </>
                    }
                </div>
                <div className="column">
                    <figure className="image">
                        <img src={medical_research} alt="" />
                    </figure>
                </div>
            </div>
        </>
    );
}

export default InitialSection;
